import React, {PureComponent , Component,  useRef, useState, useEffect } from "react";
import {
  AppState,
  View,
  StyleSheet,
  Text,
  TextInput,
  Button,
  Linking
} from "react-native";
import renderer from 'react-test-renderer';
import { diffString, diff } from 'json-diff';
import VisibleLoopTesting from './components/VisibleLoopTesting'

export default class App extends Component {
  state = {
    appState: AppState.currentState,
    applicationName: '',
    appJson:{},
    randomColor: null
  };

  getRandomColor = () =>
  {
      return 'rgb(' + (Math.floor(Math.random() * 256)) + ',' + (Math.floor(Math.random() * 256)) + ',' + (Math.floor(Math.random() * 256)) + ')';
  }

  componentDidMount() {

    AppState.addEventListener("change", this._handleAppStateChange);
  }

  componentWillUnmount() {

     AppState.addEventListener("change", this._handleAppStateChange);
  }

  _handleAppStateChange = nextAppState => {
    if (AppState.currentState.match(/active/)){
      const tree1 = renderer.create(<App />).toJSON();
      this.setState({appJson : tree1});
      console.log("App has come to the foreground!");

    }

    if (AppState.currentState.match(/inactive|background/)){
      this.getRandomColor
      Linking.openURL("exp://192.168.10.106:19000");
    }
    if (
      this.state.appState.match(/inactive|background/) &&
      nextAppState === "active"
    ) {
      console.log("App has come to the foreground!");
      const tree = renderer.create(<App />).toJSON();
      //console.log(tree);
      console.log(diffString(this.state.appJson, tree));

    }

    this.setState({ appState: nextAppState });

  };

  openApplication = () => {
    const tree = renderer.create(<App />).toJSON();
    console.log(tree);

    return tree;


}

componentWillMount()
{
    // getRandomColor method returns a new color code in RGB format so
    // get new color code from getRandomColor function and save it in
    // randomColor state variable.

    this.setState({ randomColor: this.getRandomColor() });
}

//  function called on button click
generateRandomColor = () =>
{
    this.setState({ randomColor: this.getRandomColor() });
}


  render() {
    return (
      <View style={styles.container}>





        <VisibleLoopTesting/>
      </View>

    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    padding: 30,
    backgroundColor: "#ffffff"
  },
  input: {
    width: 255,
    height: 44,
    padding: 10,
    margin: 10,
    backgroundColor: "#FFF",
    borderColor: "#000",
    borderRadius: 0.5,
    borderWidth: 0.5
  }
});
