import React, { Component, AppState } from "react";
import { View, Text, BackHandler, Linking } from "react-native";
import renderer from "react-test-renderer";
import { diffString, diff } from "json-diff";

export class BackgroundForground extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  setBackgoundAction() {
    BackHandler.exitApp();
  }

  setForgroundAction() {
    Linking.openURL("exp://192.168.10.106:19000");
  }
  componentDidMount() {
    AppState.addEventListener("change", this._handleAppStateChange);
  }

  componentWillUnmount() {
    AppState.removeEventListener("change", this._handleAppStateChange);
  }

  _handleAppStateChange = (nextAppState) => {
    if (this.state.appState.match(/active/)) {
      console.log("Application is active");
    }
    if (
      this.state.appState.match(/inactive|background/) &&
      nextAppState === "active"
    ) {
      console.log("App has come to the foreground!");
    }
    this.setState({ appState: nextAppState });
  };

  render() {
    console.log("Set Background Action");
    this.setBackgoundAction();
    console.log("Set Forground Action");
    this.setForgroundAction();
    return <Text></Text>;
  }
}

export default BackgroundForground;
