import React, {PureComponent , Component,  useRef, useState, useEffect } from "react";
import {
  AppState,
  View,
  StyleSheet,
  Text,
  TextInput,
  Button,
  Linking,

} from "react-native";
import renderer from 'react-test-renderer';
import { diffString, diff } from 'json-diff';

export default class App extends Component {
  state = {
    appState: AppState.currentState,
    applicationName: '',
    appJson:{},
    state : { inputs: ['input-0'] }
  };


  componentDidMount() {

    AppState.addEventListener("change", this._handleAppStateChange);
  }

  componentWillUnmount() {

     AppState.addEventListener("change", this._handleAppStateChange);
  }

  _handleAppStateChange = nextAppState => {
    if (AppState.currentState.match(/active/)){
      const tree1 = renderer.create(<App />).toJSON();
      this.setState({appJson : tree1});
      console.log("App has come to the foreground!");

    }

    if (AppState.currentState.match(/inactive|background/)){
        Linking.openURL("exp://192.168.10.110:19000");
        var newInput = `input-${this.state.inputs.length}`;
        this.setState(prevState => ({ inputs: prevState.inputs.concat([newInput]) }));
    }
    if (
      this.state.appState.match(/inactive|background/) &&
      nextAppState === "active"
    ) {
      console.log("App has come to the foreground!");
      const tree = renderer.create(<App />).toJSON();
      //console.log(tree);
      console.log(diffString(this.state.appJson, tree));

    }

    this.setState({ appState: nextAppState });

  };

  openApplication = () => {
    const tree = renderer.create(<App />).toJSON();
    console.log(tree);

    return tree;


}


  render() {
    return (
      <View style={styles.container}>
      <TextInput style = {styles.input}
               placeholder = "Application Name"
               placeholderTextColor = "#9a73ef"
               onChangeText = {this.applicationName}/>



        <View style={{ marginTop: 20 }}>
          <Button onPress={this.openApplication} title="Open " />
        </View>

               <Form>
                   <div id="dynamicInput">
                       {this.state.inputs.map(input => <FormInput key={input} />)}
                   </div>
               </Form>


      </View>

    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    padding: 30,
    backgroundColor: "#ffffff"
  },
  input: {
    width: 255,
    height: 44,
    padding: 10,
    margin: 10,
    backgroundColor: "#FFF",
    borderColor: "#000",
    borderRadius: 0.5,
    borderWidth: 0.5
  }
});
